    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        try {
            String name = jTextField1.getText();
            float marks = Float.parseFloat(jTextField2.getText());
            int age = Integer.parseInt(jTextField3.getText());

            MyConnection.pst.close();

            MyConnection.pst = MyConnection.con.prepareStatement("insert into student(st_name,st_marks,st_age) values(?,?,?)");

//      MyConnection.pst.setString(1,name);
//      MyConnection.pst.setFloat(2, marks);
//      MyConnection.pst.setInt(3, age);


            MyConnection.pst.setString(1, jTextField1.getText());
            MyConnection.pst.setFloat(2, Float.parseFloat(jTextField2.getText()));
            MyConnection.pst.setInt(3, Integer.parseInt(jTextField3.getText()));

            int row = MyConnection.pst.executeUpdate();

            if (row > 0) {
                //System.out.println("Data is Inserted");
                JOptionPane.showMessageDialog(null, "Inserted", "Inserted", 1);
                jTextField1.setText(null);
                jTextField2.setText(null);
                jTextField3.setText(null);
                jTextField4.setText(null);



            } else {
                //System.out.println("Data is not Inserted");
                JOptionPane.showMessageDialog(null, " Not Inserted", " NotInserted", 0);
            }

            MyConnection.pst.close();
        } catch (Exception ex) {
            System.out.println(ex);
        }


    }//GEN-LAST:event_jButton1ActionPerformed

