    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // TODO add your handling code here:
        try {
            int rollno = Integer.parseInt(jTextField4.getText());
            MyConnection.pst.close();

            MyConnection.pst = MyConnection.con.prepareStatement("delete from student where st_rollno=?");
            MyConnection.pst.setInt(1, rollno);

            int row = MyConnection.pst.executeUpdate();
            if (row > 0) {
                //System.out.println("Data is Inserted");
                JOptionPane.showMessageDialog(null, "Deleted", "Deleted", 1);
            } else {
                //System.out.println("Data is not Inserted");
                JOptionPane.showMessageDialog(null, " Not Deleted", " Not Deleted", 0);
            }

            MyConnection.pst.close();

        } catch (Exception ex) {
            System.out.println(ex);
        }

    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        // TODO add your handling code here:
        try {


            int rollno = Integer.parseInt(jTextField4.getText());
            MyConnection.pst.close();

            MyConnection.pst = MyConnection.con.prepareStatement("select * from student where st_rollno=?");
            MyConnection.pst.setInt(1, rollno);

            rs = MyConnection.pst.executeQuery();

            if (rs.next()) {



                jTextField1.setText(rs.getString("st_name").trim());
                jTextField2.setText(String.valueOf(rs.getFloat("st_marks")));
                jTextField3.setText(String.valueOf(rs.getInt("st_age")));

            } else {
                JOptionPane.showMessageDialog(null, " Not Data Found", " Not Deleted", 0);
            }




            MyConnection.pst.close();
        } catch (Exception ex) {
            System.out.println(ex);
        }



    }//GEN-LAST:event_jButton4ActionPerformed

