    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:

        try {
            int rollno = Integer.parseInt(jTextField4.getText());
            String name = jTextField1.getText();
            float marks = Float.parseFloat(jTextField2.getText());
            int age = Integer.parseInt(jTextField3.getText());

            MyConnection.pst.close();

            MyConnection.pst = MyConnection.con.prepareStatement("update student set st_name=?,st_marks=?,st_age=? where st_rollno=?");
            
            
            MyConnection.pst.setString(1, name);
            MyConnection.pst.setFloat(2, marks);
            MyConnection.pst.setInt(3, age);
            MyConnection.pst.setInt(4, rollno);


            int row = MyConnection.pst.executeUpdate();
            if (row > 0) {
                //System.out.println("Data is Inserted");
                JOptionPane.showMessageDialog(null, "Data is Updated", "Updated", 1);
            } else {
                //System.out.println("Data is not Inserted");
                JOptionPane.showMessageDialog(null, "Data is not Updated", " Not Updated", 0);
            }

            MyConnection.pst.close();

        } catch (Exception ex) {
            System.out.println(ex);
        }



    }//GEN-LAST:event_jButton2ActionPerformed

